mapper.insert:
INSERT INTO T_BOARD( BOANUM, BOATITLE, BOACONTENT, USERID, GRONUM
,ORIGINALFILE, SAVEDFILE
)VALUES( T_BOARD_SEQ.NEXTVAL, #{boaTitle}, #{boaContent}, #{userId}, #{groNum}
,#{originalFile}, #{savedFile}
)

mapper.select:
SELECT *FROM T_BOARD 
WHERE
BOANUM=#{boaNum} 

AND GRONUM=#{groNum}
