mapper.insert:
INSERT INTO T_REQUEST(
REQNUM,
REQUESTER,
REQACCEPTER
)VALUES(
T_REQUEST_SEQ.NEXTVAL,
#{requester},
#{reqAccepter}	
)

mapper.select:
SELECT *FROM T_REQUEST
WHERE REQUESTER=#{requester}
OR REQACCEPTER=#{reqAccepter}

SELECT *FROM T_REQUEST
WHERE ISACCEPTED=0
AND REQACCEPTER=#{reqAccepter}

SELECT *FROM T_REQUEST
WHERE ISACCEPTED=1
AND REQACCEPTER=#{reqAccepter}

mapper.update:
UPDATE T_REQUEST
SET
ISACCEPTED=1
WHERE REQACCEPTER=#{reqAccepter}
AND REQUESTER=#{requester}

mapper.delete:
DELETE FROM T_REQUEST 
WHERE REQACCEPTER=#{reqAccepter}
AND REQUESTER=#{requester}
